    @mock_comment_submission ||= mock_model(CommentSubmission, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all comment_submissions as @comment_submissions" do
      CommentSubmission.stub(:all) { [mock_comment_submission] }
      get :index
      assigns(:comment_submissions).should eq([mock_comment_submission])
    end
  end

  describe "GET show" do
    it "assigns the requested comment_submission as @comment_submission" do
      CommentSubmission.stub(:find).with("37") { mock_comment_submission }
      get :show, :id => "37"
      assigns(:comment_submission).should be(mock_comment_submission)
    end
  end

  describe "GET new" do
    it "assigns a new comment_submission as @comment_submission" do
      CommentSubmission.stub(:new) { mock_comment_submission }
      get :new
      assigns(:comment_submission).should be(mock_comment_submission)
    end
  end

  describe "GET edit" do
    it "assigns the requested comment_submission as @comment_submission" do
      CommentSubmission.stub(:find).with("37") { mock_comment_submission }
      get :edit, :id => "37"
      assigns(:comment_submission).should be(mock_comment_submission)
    end
  end

  describe "POST create" do
