      it "assigns a newly created comment_submission as @comment_submission" do
        CommentSubmission.stub(:new).with({'these' => 'params'}) { mock_comment_submission(:save => true) }
        post :create, :comment_submission => {'these' => 'params'}
        assigns(:comment_submission).should be(mock_comment_submission)
      end

      it "redirects to the created comment_submission" do
        CommentSubmission.stub(:new) { mock_comment_submission(:save => true) }
        post :create, :comment_submission => {}
        response.should redirect_to(comment_submission_url(mock_comment_submission))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved comment_submission as @comment_submission" do
        CommentSubmission.stub(:new).with({'these' => 'params'}) { mock_comment_submission(:save => false) }
        post :create, :comment_submission => {'these' => 'params'}
        assigns(:comment_submission).should be(mock_comment_submission)
      end

      it "re-renders the 'new' template" do
        CommentSubmission.stub(:new) { mock_comment_submission(:save => false) }
        post :create, :comment_submission => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested comment_submission" do
        CommentSubmission.stub(:find).with("37") { mock_comment_submission }
        mock_comment_submission.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :comment_submission => {'these' => 'params'}
