      end

      it "redirects to the comment_submission" do
        CommentSubmission.stub(:find) { mock_comment_submission(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(comment_submission_url(mock_comment_submission))
      end
    end

    describe "with invalid params" do
      it "assigns the comment_submission as @comment_submission" do
        CommentSubmission.stub(:find) { mock_comment_submission(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:comment_submission).should be(mock_comment_submission)
